,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'function render(',
'  p_region              in apex_plugin.t_region,',
'  p_plugin              in apex_plugin.t_plugin,',
'  p_is_printer_friendly in boolean) return apex_plugin.t_region_render_result is',
'',
'  query_result          apex_plugin_util.t_column_value_list;',
'  type query_result_colors is table of varchar2(30) index by varchar2(30);',
'  nl query_result_colors;',
'  width            varchar2(4000);',
'  height           varchar2(4000);',
'  no_data_found    varchar2(4000);',
'  region_id        varchar2(4000);',
'  cnt              varchar2(4000);',
'  js               clob;',
'begin',
'  width := nvl(p_region.attribute_01, ''100%'');  ',
'  height := nvl(p_region.attribute_02, ''44px'');  ',
'  region_id := nvl(p_region.attribute_03, ''plugin_cloud_of_words'');',
'  no_data_found := nvl(p_region.attribute_04, ''No data found'');  ',
'    ',
'  query_result := apex_plugin_util.get_data (',
'    p_sql_statement      => p_region.source,',
'    p_min_columns        => 1,',
'    p_max_columns        => 20,',
'    p_component_name     => p_region.name,',
'    p_search_type        => null,',
'    p_search_column_name => null,',
'    p_search_string      => null);  ',
'      ',
'  if query_result(1).first is not null then',
'      ',
'    /* We create a block to display the tag cloud, adjust the width and height. */',
'    htp.print(''<div id="''||region_id||''" style="width:''||width||''; height:''||height||'';"></div>'');',
'    ',
'    js := js||''var word_array = ['';',
'      ',
'    for i in query_result(1).first .. query_result(1).last loop                          ',
'        if query_result(3)(i) is not null then        ',
'          js := js||''{text: "''||query_result(1)(i)||''", weight: ''||query_result(2)(i)||'', html: {class:"word_''||query_result(3)(i)||''"} },'';',
'        else',
'          js := js||''{text: "''||query_result(1)(i)||''", weight: ''||query_result(2)(i)||''},'';',
'        end if;',
'                  ',
'        if query_result(3)(i) is not null then',
'          nl(query_result(3)(i)) := query_result(3)(i);',
'        end if;',
'    end loop;',
'',
'    js := js||''];(function(){$("#''||region_id||''").jQCloud(word_array)})();'';',
'    ',
'    apex_javascript.add_onload_code(js);    ',
'      ',
'      ',
'    cnt := nl.first;',
'    /* Styles, coloring of words. */',
'    htp.print(''<style>'');',
'    while cnt is not null ',
'    loop',
'      htp.print(''.word_''||nl(cnt)||''{color:#''||nl(cnt)||'' !important;}'');',
'      cnt := nl.next(cnt);',
'    end loop;',
'    htp.print(''</style>'');',
'  else',
'    htp.print(''<div id="''||region_id||''" style="width:''||width||''; height:''||height||'';">',
'                <span class="nodatafound">''||no_data_found||''</span>',
'           </div>'');',
'  end if;',
'    ',
'  return null;',
'    ',
'  exception when others then ',
'    htp.print(''<div id="''||region_id||''" style="width:''||width||''; height:''||height||'';">',
'                <span class="nodatafound">Error:''||sqlcode||'' - ''||sqlerrm||''</span>',
'           </div>'');',
'    return null;',
'end;'))
